import React, { useState } from 'react';
import '../../assets/Portfolio/testimonial.css'

const Testimonial = ({ quote, author, image }) => {
  return (
    <div className="testimonial">
      <div className="testimonial-image">
        <img src={image} alt={author} />
      </div>
      <div className="testimonial-content">
        <p className="testimonial-quote"> <i class="fas fa-quote-left" id="qutoes"></i>{quote}<i class="fas fa-quote-right" id="qutoes"></i></p>
        <p className="testimonial-author">{author}</p>
      </div>
    </div>
  );
};

const ClientTestimonials = () => {
  const testimonials = [
    {
      quote: 'We were blown away by the quality of the laser cutting services provided by this company. The attention to detail and precision were exceptional.',
      author: 'John Doe, CEO of Arassan Corporation',
      image: 'data:image/jpeg;base64,',
    },
    {
      quote: 'We were impressed by the timely delivery of our project. The team was very responsive & kept us informed throughout the process and performance',
      author: 'Jane Smith, Marketing Manager of Basetick Inc.',
      image: 'data:image/jpeg;base64,',
    },
    {
      quote: 'The laser cutting services provided by this company exceeded our expectations. The quality of the work was outstanding and the price was very competitive.',
      author: 'Bob Johnson, Owner of somrgety Enterprises',
      image: 'data:image/jpeg;base64,',
    },
    {
      quote: 'We were blown away by the quality of the laser cutting services provided by this company. The attention to detail and precision were exceptional.',
      author: 'John Doe, CEO of Zingeefi Corporation',
      image: 'data:image/jpeg;base64,',
    },
    {
      quote: 'We were impressed by the timely delivery of our project. The team was very responsive and kept us informed throughout the process and performance',
      author: 'Johnson, Marketing Manager of winting Inc.',
      image: 'data:image/jpeg;base64,',
    },
    {
      quote: 'The laser cutting services provided by this company exceeded our expectations. The quality of the work was outstanding and the price was very competitive.',
      author: 'Bob Steve, Owner of ExtraCorporate Enterprises',
      image: 'data:image/jpeg;base64,',
    },
  ];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [slidesToShow, setSlidesToShow] = useState(1);

  const handleNext = () => {
    setCurrentIndex((currentIndex + 1) % testimonials.length);
  };

  const handlePrev = () => {
    setCurrentIndex((currentIndex - 1 + testimonials.length) % testimonials.length);
  };

  const handleResize = () => {
    if (window.innerWidth <= 480) {
      setSlidesToShow(1);
      setCurrentIndex(0);
    } else if (window.innerWidth <= 768) {
      setSlidesToShow(2);
      setCurrentIndex(0);
    } else {
      setSlidesToShow(3);
      setCurrentIndex(0);
    }
  };

  React.useEffect(() => {
    handleResize();
    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <div className="client-testimonials">
      <h2>Client Testimonials</h2>
      <div className="testimonial-slider">
        <div className="testimonial-slider-content">
          {testimonials.slice(currentIndex, currentIndex + slidesToShow).map((testimonial, index) => (
            <div key={index} className="testimonial-slider-item">
              <Testimonial {...testimonial} />
            </div>
          ))}
        </div>
        <div className="testimonial-slider-controls">
          <button className="testimonial-slider-prev" onClick={handlePrev}>
            <i className="fas fa-chevron-left" />
          </button>
          <button className="testimonial-slider-next" onClick={handleNext}>
            <i className="fas fa-chevron-right" />
          </button>
        </div>
      </div>
    </div>
  );
};

export default ClientTestimonials;